mapper.update:
UPDATE
VERSION = #{version,jdbcType=TINYINT} + 1, UNSETTLE_AMOUNT = #{unsettleAmount,jdbcType=DECIMAL}, USABLE_AMOUNT = #{usableAmount,jdbcType=DECIMAL}, ALTER_AMOUNT = #{alterAmount,jdbcType=DECIMAL}, ALTER_UNSETTLE_AMOUNT = #{alterUnsettleAmount,jdbcType=DECIMAL}, ALTER_USABLE_AMOUNT = #{alterUsableAmount,jdbcType=DECIMAL}, ACCOUNT_PROCESS_NO = #{accountProcessNo,jdbcType=VARCHAR}, TRX_NO = #{trxNo,jdbcType=VARCHAR}, MCH_TRX_NO = #{mchTrxNo,jdbcType=VARCHAR}, TRX_TIME = #{trxTime,jdbcType=TIMESTAMP}, BIZ_TYPE = #{bizType,jdbcType=SMALLINT}, REMARK = #{remark,jdbcType=VARCHAR}, PROCESS_TYPE = #{processType,jdbcType=TINYINT}, DEBIT_COMMIT_STAGE = #{debitCommitStage,jdbcType=TINYINT}, EXTRA_INFO = #{extraInfo,jdbcType=OTHER}
WHERE ID = #{id,jdbcType=BIGINT} and VERSION = #{version,jdbcType=TINYINT}

UPDATE

SET VERSION=VERSION+1,
DEBIT_COMMIT_STAGE = #{newStage,jdbcType=TINYINT}
WHERE
(MERCHANT_NO,TRX_NO) IN

    (#{item.merchantNo,jdbcType=VARCHAR},#{item.trxNo,jdbcType=VARCHAR})

AND PROCESS_TYPE=#{processType,jdbcType=TINYINT}
AND DEBIT_COMMIT_STAGE = #{oldStage,jdbcType=TINYINT}

mapper.select:
SELECT count(ID) FROM

WHERE (MERCHANT_NO,TRX_NO) IN

    (#{item.merchantNo,jdbcType=VARCHAR},#{item.trxNo,jdbcType=VARCHAR})

AND PROCESS_TYPE=#{processType,jdbcType=TINYINT}
AND DEBIT_COMMIT_STAGE = #{debitCommitStage,jdbcType=TINYINT}

SELECT ID from

WHERE CREATE_TIME  <=  #{migrateEndDate,jdbcType=TIMESTAMP}
AND ID  <=  #{maxId,jdbcType=BIGINT}
AND DEBIT_COMMIT_STAGE IN
#{_item,jdbcType=TINYINT}
order by ID asc
limit #{migrateNumPerTime,jdbcType=BIGINT}
